"use strict";
//Offline mode service worker implementation

const CACHE_NAME = 'WebCamGallery';
const RESOURCES = [
	'',
	'WebCamGallery',
	'lib/bootstrap/dist/css/bootstrap.css',
	'css/site.css',
	'lib/blueimp-gallery/css/blueimp-gallery.min.css',
	'lib/video.js/dist/video-js.min.css',
	'lib/jquery/dist/jquery.js',
	'lib/bootstrap/dist/js/bootstrap.bundle.min.js',
	'js/site.js',
	'lib/blueimp-gallery/js/blueimp-gallery.min.js',
	'lib/video.js/dist/video.min.js'
];

self.addEventListener('install', function (event) {
	const domain = new URL(location).searchParams.get('domain');

	event.waitUntil(
		caches.open(CACHE_NAME).then(function (cache) {
			return cache.addAll(RESOURCES.map(res => domain + res));
		})
	);
});

self.addEventListener('fetch', event => {
	event.respondWith(caches.match(event.request).then(cached_response => {
		// caches.match() always resolves
		// but in case of success response will have value
		// if (response)
		// 	return response;

		return fetch(event.request).then(response => {
			// Check if we received a valid response
			if (!response || response.status !== 200 || response.type !== 'basic') {
				return response || fetch(event.request);
			}

			if (event.request.method !== 'GET')
				return response;

			// response may be used only once
			// we need to save clone to put one copy in cache
			// and serve second one
			let responseClone = response.clone();

			caches.open(CACHE_NAME).then(cache => {
				cache.put(event.request, responseClone);
			});
			return response;
		}).catch(() => {
			return cached_response;
		});

	}));
});
